import './App.css';
import React, { Component } from 'react';
import { Route, Routes, NavLink, BrowserRouter as Router } from "react-router-dom"
import axios from 'axios'
import AddProduct from './components/AddProduct';
import Cart from './components/Cart';
import Login from './components/Login';
import ProductList from './components/ProductList';
import Context from "./Context"
import { paintingData, signInUser } from './Firebase';

export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      user: null,
      cart: {},
      products: []
    }
    this.routerRef = React.createRef()
  }

  async componentDidMount() {
    let user = {}
    let cart = []

    const products = paintingData

    this.setState({ user, products: products, cart })
  }

  login = async (username) => {
    const res = await signInUser()
    .then((cred) => {
      console.log('user created', cred.user)
    })
    .catch((res) => {
      return { status: 401, message: 'Unauthorized'}
    })

    if(res.status === 200) {
      const user = {
        username
      }

      this.setState({ user })
      return true
    } else {
      return false
    }
  }

  logout = e => {
    e.preventDefault()
    this.setState({ user: null })
  }

  addToCart = cartItem => {
    let cart = this.state.cart;
    if (cart[cartItem.title]) {
      cart[cartItem.title].amount += cartItem.amount;
    } else {
      cart[cartItem.title] = cartItem;
    }
    if (cart[cartItem.title].amount > cart[cartItem.title].product.stock) {
      cart[cartItem.title].amount = cart[cartItem.title].product.stock;
    }
    this.setState({ cart });
  };


  removeFromCart = cartItemId => {
    let cart = this.state.cart
    delete cart[cartItemId]
    this.setState({ cart })
  }

  clearCart = () => {
    let cart = {}
    localStorage.removeItem("cart")
    this.setState({ cart })
  }

  checkout = () => {
    if (!this.state.user) {
      this.routerRef.current.history.push("/login")
      return
    }

    const cart = this.state.cart

    const products = this.state.products.map(p => {
      if (cart[p.title]) {
        p.stock = p.stock - cart[p.title].amount

        axios.put(
          `http://localhost:3001/products/${p.id}`,
          { ...p },
        )
      }
      return p
    })

    this.setState({ products })
    this.clearCart()
  }

render() {
  return (
  <Context.Provider
    value={{
      ...this.state,
      removeFromCart: this.removeFromCart,
      addToCart: this.addToCart,
      login: this.login,
      clearCart: this.clearCart,
      checkout: this.checkout
    }}
  >
    <Router ref={this.routerRef}>
      <div className='App'>
        <nav
          className='navbar container'
          role='navigation'
          aria-label='main navigation'
        >
        <div className='navbar-brand'>
          <b>eCommerce Paintings</b>
          <label
            role='button'
            class='navbar-burger burger'
            aria-label='menu'
            aria-expanded='false'
            data-target='navbarBasicExample'
            onClick={e => {
              e.preventDefault()
              this.setState({ showMenu: !this.state.showMenu })
            }}
          >
            <span aria-hidden="true"></span>
            <span aria-hidden="true"></span>
            <span aria-hidden="true"></span>
          </label>
        </div>
            <div className={`navbar-menu ${
              this.state.showMenu ? "is-active" : ""
              }`}>
              <NavLink to="/products" className={({ isActive }) => 
                      (isActive ? "active-nav navbar-item" : "navbar-item")}>
                Products
              </NavLink>
              <NavLink to="/cart" className={({ isActive }) => 
                      (isActive ? "active-nav navbar-item" : "navbar-item")}>
                Cart
                <span
                  className='tag is-primary'
                  style={{ marginLeft: "5px" }}
                >
                  { Object.keys(this.state.cart).length }
                </span>
              </NavLink>
              {!this.state.user ? (
                <NavLink to="/login" className={({ isActive }) => 
                      (isActive ? "active-nav navbar-item" : "navbar-item")}>
                  Login
                </NavLink>
              ) : (
                <NavLink to="/" onClick={this.logout} className={({ isActive }) => 
                      (isActive ? "active-nav navbar-item" : "navbar-item")}>
                  Logout
                </NavLink>
              )}
            </div>
          </nav>
          <Routes>
            <Route exact path="/" element={<ProductList />} />
            <Route exact path="/login" element={<Login />} />
            <Route exact path="/cart" element={<Cart />} />
            <Route exact path="/add-product" element={<AddProduct />} />
            <Route exact path="/products" element={<ProductList />} />
          </Routes>
        </div>
      </Router>
    </Context.Provider>
    );
  }
}
